 create table pauta (id integer generated by default as identity, data_criacao_pauta timestamp, descricao varchar(255), status_pauta integer, primary key (id))
 create table sessao_votacao (pauta_id integer not null, data_hora_inicio_sessao timestamp, tempo_prazo varchar(255), primary key (pauta_id))
 create table usuario (id integer generated by default as identity, cpf varchar(255), email varchar(255), nome varchar(255), primary key (id))
 create table voto (id integer generated by default as identity, data_hora_voto timestamp, voto integer, id_sessao integer, id_usuario integer, primary key (id))